import React, { Component } from 'react'
import DonorService from '../services/DonorService'
import PersonService from '../services/PersonService';
import SeekerService from '../services/SeekerService';
import { NavLink } from 'react-router-dom';
import undertaking from '../images/UNDERTAKING.pdf';

export default class ListDonorComponent extends Component {


	constructor(props) {
		super(props)
		this.state = this.initialState;
		this.addDonor = this.addDonor.bind(this);
		this.editUser = this.editUser.bind(this);
		this.logout = this.logout.bind(this);
		this.getDonorsForSeekerByBloodGroup = this.getDonorsForSeekerByBloodGroup.bind(this);

	}

	initialState = {
		donor: {}
	}




	componentDidMount() {

		// console.log(window.localStorage.getItem("emailId"));
		if (window.localStorage.getItem("role") === "DONOR") {
			DonorService.getDonor(window.localStorage.getItem("emailId")).then((res) => {
				// console.log(res.data.result);
				this.setState({ donor: res.data.result });
				// console.log(window.localStorage.getItem("emailId"));
				// console.log(this.state.donor);
			});	
		}
		if (window.localStorage.getItem("role") === "SEEKER") {
			console.log(window.localStorage.getItem("emailId"));
			SeekerService.getSeeker(window.localStorage.getItem("emailId")).then((res) => {
				// console.log(res.data.result);
				this.setState({ donor: res.data.result });
				// console.log(window.localStorage.getItem("emailId"));
				// console.log(this.state.donor);
			});
		}

	}

	addDonor() {
		this.props.history.push('/donor_registration');
	}

	editUser() {
		if (window.localStorage.getItem("role") ==="DONOR"){
			this.props.history.push('/edit_donor');
		}
		if (window.localStorage.getItem("role") ==="SEEKER"){
			this.props.history.push('/edit_seeker');
		}
	}
	logout() {
		alert("Goodbye, " + this.state.donor.firstName)
		window.localStorage.removeItem("emailId");
		console.log(this.setState(this.initialState));
		this.props.history.push('/');
	}

	deleteUser() {
		// window.localStorage.removeItem("emailId");
		// console.log(this.setState(this.initialState));
		PersonService.deletePersonDetails(window.localStorage.getItem("emailId")).then(
			res => {
				alert("We feel sad to see you go.")
				window.localStorage.removeItem("emailId");
				// console.log(this.setState(this.initialState));
				this.props.history.push('/');
			}
		)

	}
	getDonorsForSeekerByBloodGroup(){
		this.props.history.push("/search");
	}


	render() {
		return (
			<div>
				{/* Banner */}
				<section id="banner">
                <h1 className="text-center" style={{ textTransform: 'uppercase', fontWeight: 525 }}>Hello, {this.state.donor.firstName}</h1>
                <p style={{fontSize: 'x-large', fontWeight: 325}}>Donate, Find Convalescent Plasma Across The Nation</p>
            </section>
			{/* <div >
			<header>
				<br />
				<br />
				<h2 style={{fontWeight: 'bold', textAlign: 'center'}}>{this.state.donor.firstName} {this.state.donor.lastName}, {this.state.donor.age}
				({this.state.donor.bloodGroup}) <br /></h2>
			</header>
			</div>
			<br /> */}
			<div>
			{/* <table className="table table-stripped table-bordered">
						<thead>
							<tr>


								<th>First Name</th>
								<th>Last Name</th>
								<th>Phone Number</th>
								<th>Age</th>
								<th>Email ID</th>
								<th>Password</th>
								<th>State Name</th>
								<th>Blood Group</th>
								<th>Last Date of Symptom</th>
							</tr>
						</thead>
						<tbody>
							{
								// this.state.map(
								// 	donor =>
								<tr >
									<td> {this.state.donor.firstName} </td>
									<td> {this.state.donor.lastName} </td>
									<td> {this.state.donor.phoneNumber} </td>
									<td> {this.state.donor.age} </td>
									<td> {this.state.donor.email} </td>
									<td> {this.state.donor.password} </td>
									<td> {this.state.donor.stateName} </td>
									<td> {this.state.donor.bloodGroup} </td>
									<td> {this.state.donor.lastSymptomDate}</td>
								</tr>

								// )
							}
						</tbody>
						<tr>
						<td><div className="otherbuttons" >
						<button onClick={this.editUser}>Edit Details</button>
						</div>
						</td>
						<td className="otherbuttons">
						<button onClick={this.deleteUser}>Delete Profile</button>
						</td>
						<td><div className="otherbuttons">
						<button onClick={this.logout}>Logout</button>
						</div>
						</td>

						{/* <td><div className="otherbuttons">
						<button type="submit" onClick={this.addDonor}>Add Donor</button>
						</div>
						</td> 
						</tr>
					</table> */}

					<table >
						<thead>
							<tr style={{ textTransform: 'uppercase', fontWeight: 650, fontSize: 30, textAlign: 'center', verticalAlign: 'center' }}>
								{/*<th>Donor Id</th>*/}
								<td colspan="3" >
									<br />
									{this.state.donor.firstName}{' '}
									{this.state.donor.lastName}{',                         '}
									{this.state.donor.age}{',                  '}
									({this.state.donor.bloodGroup})<br /><br />
								</td>
							</tr>
						</thead>
						<tbody>
							{
								// this.state.map(
								// 	donor =>
								<tr style={{ fontWeight: 600, fontSize: 25, textAlign: 'center', verticalAlign: 'center' }}>
									{/*<td> Donor Id </td>*/}
									<td>e-mail address:{' '}{this.state.donor.email}</td>
									<td>Contact:{' '}(+91){' '}{this.state.donor.phoneNumber}</td>
									<td>Location:{' '} {this.state.donor.stateName}</td>
									<br /><br />
								</tr>

							}
						</tbody>
					</table><br />

				</div>

				<div style={{ textAlign: 'center' }}>
					<button style={{ backgroundColor: '#7acdf1' }} onClick={this.editUser}>Edit Details</button>{' '}
					<button style={{ backgroundColor: '#7acdf1' }} onClick={this.logout}>Logout</button>{' '}
					<button style={{ backgroundColor: '#E14141' }} onClick={this.deleteUser}>Delete Profile</button>
				</div>

						<br />
					<p style={{ textAlign: 'center'}}>Users MUST read and duly sign the completely filled  <NavLink to={undertaking} target="_blank">undertaking form</NavLink> at the time donors report at plasma receiver's 
					location; BEFORE undergoing the transfusion process.</p>



			</div>
		)
	}
}
