import React, { Component } from 'react'
import DonorService from '../services/DonorService'
import PersonService from '../services/PersonService';
import SeekerService from '../services/SeekerService';
import { NavLink } from 'react-router-dom';
import undertaking from '../images/UNDERTAKING.pdf';

export default class ListDonorComponent extends Component {


	constructor(props) {
		super(props)
		this.state = this.initialState;
		this.addDonor = this.addDonor.bind(this);
		this.editUser = this.editUser.bind(this);
		this.logout = this.logout.bind(this);
		this.getDonorsForSeekerByBloodGroup = this.getDonorsForSeekerByBloodGroup.bind(this);
		this.requestsMade = this.requestsMade.bind(this);
		this.showDonorDetails = this.showDonorDetails.bind(this);
	}

	initialState = {
		donors: [],
		seeker: []
	}




	componentDidMount() {

		// console.log(window.localStorage.getItem("emailId"));
		if (window.localStorage.getItem("role") === "DONOR") {
			DonorService.getDonor(window.localStorage.getItem("emailId")).then((res) => {
				// console.log(res.data.result);
				this.setState({ donor: res.data.result });
				// console.log(window.localStorage.getItem("emailId"));
				// console.log(this.state.donor);
			});	
			this.requestsMade();
		}
		// if (window.localStorage.getItem("role") === "SEEKER") {
		// 	console.log(window.localStorage.getItem("emailId"));
		// 	SeekerService.getSeeker(window.localStorage.getItem("emailId")).then((res) => {
		// 		// console.log(res.data.result);
		// 		this.setState({ donor: res.data.result });
		// 		// console.log(window.localStorage.getItem("emailId"));
		// 		// console.log(this.state.donor);
		// 	});
		// }

	}

	requestsMade() {
		SeekerService.seekerGetAllDonors(window.localStorage.getItem("emailId"))
			.then((res) => {
				// console.log(res);
				// console.log(res.data.result);
				this.setState({ donors: res.data.result });
				// if(this.state.donor.requestStatus.length===0){
				// 	console.log("empty");
				// }
				// console.log(this.state.donors.requestStatus);
			});
	}

	addDonor() {
		this.props.history.push('/donor_registration');
	}

	editUser() {
		if (window.localStorage.getItem("role") ==="DONOR"){
			this.props.history.push('/edit_donor');
		}
		if (window.localStorage.getItem("role") ==="SEEKER"){
			this.props.history.push('/edit_seeker');
		}
	}
	logout() {
		alert("Goodbye, " + this.state.donor.firstName)
		window.localStorage.removeItem("emailId");
		console.log(this.setState(this.initialState));
		this.props.history.push('/');
	}

	deleteUser = (e) => {
		//e.preventDefault();
		// window.localStorage.removeItem("emailId");
		// console.log(this.setState(this.initialState));

		PersonService.deletePersonDetails(window.localStorage.getItem("emailId")).then(
			res => {
				alert("We feel sad to see you go.")
				window.localStorage.removeItem("emailId");
				// console.log(this.setState(this.initialState));
				this.props.history.push("/");
			}
		)

	}
	getDonorsForSeekerByBloodGroup(){
		this.props.history.push("/search");
	}

	showDonorDetails(requestStatus, donorEmail) {
		if (requestStatus === 1) {
			console.log("request not accepted yet");
		}
		if (requestStatus === 2) {
			window.localStorage.setItem("donorEmail", donorEmail);
			this.props.history.push("/donor_details");
		}
		if (requestStatus === 3){
			console.log("request Rejected");
		}

	}


	render() {
		return (
			<div>
				{/* Banner */}
				<section id="banner">
                <h1 className="text-center" style={{ textTransform: 'uppercase', fontWeight: 525 }}>Hello, {this.state.donors.firstName}</h1>
                <p style={{fontSize: 'x-large', fontWeight: 325}}>Donate, Find Convalescent Plasma Across The Nation</p>
            </section>
			<div >
			<header>
				<br />
				<br />
				<h2 style={{fontWeight: 'bold', textAlign: 'center'}}>{this.state.donors.firstName} {this.state.donors.lastName}, {this.state.donors.age}
				({this.state.donors.bloodGroup}) <br /></h2>
			</header>
			</div>
			<br />
			<div>
			<table className="table table-stripped table-bordered">
						<thead>
							<tr>


								<th>First Name</th>
								<th>Last Name</th>
								<th>Phone Number</th>
								<th>Age</th>
								<th>Email ID</th>
								{/* <th>Password</th> */}
								<th>State Name</th>
								<th>Blood Group</th>
								<th>Last Date of Symptom</th>
							</tr>
						</thead>
						<tbody>
							{
								// this.state.map(
								// 	donor =>
								<tr >
									<td> {this.state.donor.firstName} </td>
