import React, { Component } from 'react'
import SeekerService from '../services/SeekerService';

export default class ListDonorComponent extends Component {
	constructor(props) {
		super(props)
		this.state = {
			bloodGroup: '',
			stateName: '',
			donors: []
		}
		this.onChange = this.onChange.bind(this);
		this.search = this.search.bind(this);
		this.displayAllSeekerDetails = this.displayAllSeekerDetails.bind(this);

	}

	onChange = (e) => {
		this.setState({ [e.target.name]: e.target.value })
	}

	componentDidMount() {
			this.displayAllSeekerDetails();
	}

	displayAllSeekerDetails(){
		SeekerService.SeekerGetAllDonors()
			.then((res) => {
				this.setState({donors: res.data.result});
				// console.log(this.state.donor);
			});
	}

	searchByBoth = (e) => {
		e.preventDefault();

		const bloodGroup = this.state.bloodGroup;
		const stateName = this.state.stateName;

		if (bloodGroup !== '' && stateName !== '') {
			SeekerService.getDonorsForSeekerByBloodGroupAndStateName(bloodGroup, stateName).then(res => {
				this.setState({ donors: res.data.result });
				if (this.state.donors.length === 0) {
					alert("No Donor found with matching criteria");
