import React, { Component } from 'react'; 
import './Stylesheet.css';
import Contact from './ContactData';

const emailRegex = RegExp(
  /^[a-zA-Z0-9.!#$%&â€™*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/
);

const formValid = formErrors => {
  let valid = true;

  Object.values(formErrors).forEach(element => {
    element.length > 0 && (valid = false);
  });
  return valid;
};



class SeekerRegistration extends Component {


  constructor(props) {
    super(props)

    this.state = {
      name : "",
      gender : "",
      age: "",
      phone: "",
      bloodgroup: "",
      state: "",
      email: "",
      password: "",
      testDate:"" ,
      seekingrn: "",
      confirmedbylab: "",
      formErrors: {
        name : "",
        gender : "",
        age: "",
        phone: "",
        bloodgroup: "",
        state: "",
        email: "",
        password: "",
        testDate:"" ,
        seekingrn: "",
        confirmedbylab: "",  
      }
    }
    // this.inputRef= React.createRef()

  }

  handleChange = e => {
    e.preventDefault();
    const { key, value } = e.target;
    let formErrors = this.state.formErrors;

    switch (key) {
      case 'name':
        formErrors.name = value.length < 3 ? "minimum 3 characters required" : "";
        break;
      case 'gender':
        formErrors.gender = value.length < 1 ? "please choose an option" : "";
        break;
      case 'age':
        formErrors.age = value.length < 1 ? "minimum 2 digit required" : "";
        break;
      case 'phone':
        formErrors.phone = value.length < 10 ? "minimum 10 digits required" : "";
        break;
      case 'bloodgroup':
        formErrors.bloodgroup = value.length < 1 ? "minimum 1 character required" : "";
        break;
      case 'city':
        formErrors.city = value.length < 2 ? "minimum 2 characters required" : "";
        break;
      case 'email':
        formErrors.email = emailRegex.test(value) ? "" : "Invalid Email";
        break;
      case 'password':
        formErrors.password = value.length <6 ? "minimum 6 characters required" : "";
        break;
      default:
        break;
    }

    this.setState({ formErrors, [key]: value }, () => console.log(this.state));
  };

  handleSubmit = submit => {
     submit.preventDefault();
    if (formValid(this.state.formErrors)) {
    
      let info = {
        name: this.state.name,
        phone: this.state.phone,
        age: this.state.age,
        bloodgroup: this.state.bloodgroup,
        days: this.state.days,
        city: this.state.city,
        email: this.state.email,
        password: this.state.password
      };
	  console.log('harshal',info);
      

    }
    else {
      console.error("Form Invalid");
    }
  };

  componentDidMount(){
    // this.inputRef.current.focus()
  }


  render() {
    const { formErrors } = this.state;
    return (
      <div>
        <Contact/>
      <div className="wrappers2">
        <div className="form-wrappers">
          <h2>Patient Registration</h2>
          <form onSubmit={this.handleSubmit} autoComplete="off">
            <div className="firstName">
              <label htmlFor="firstName">First Name</label>
              <input
                type="text"
                className={formErrors.name.length > 0 ? "error" : null}
                placeholder="First Name"
                name="firstName"
                ref={this.inputRef}
                onChange={this.handleChange}
              />
              {formErrors.name.length > 0 && (
                <span className="errorMessage">{formErrors.name}</span>
              )}
            </div>
            {/* <div className="lastName">
              <label htmlFor="lastName">Last Name</label>
              <input
                type="text"
                className={formErrors.name.length > 0 ? "error" : null}
                placeholder="Last Name"
                name="lastName"

                onChange={this.handleChange}
              />
              {formErrors.name.length > 0 && (
                <span className="errorMessage">{formErrors.name}</span>
              )}
            </div> */}
            <div className="email">
              <label htmlFor="email">Phone No:</label>
              <input
                className={formErrors.phone.length > 0 ? "error" : null}
                placeholder="phone"
                type="number"
                name="phone"
                onChange={this.handleChange}
              />
              {formErrors.phone.length > 0 && (
                <span className="errorMessage">{formErrors.phone}</span>
              )}
            </div>
            <div className="age">
              <label htmlFor="age">Age</label>
              <input
                type="text"
                className={formErrors.age.length > 0 ? "error" : null}
                placeholder="Age"
                name="age"

                onChange={this.handleChange}
              />
              {formErrors.name.length > 0 && (
                <span className="errorMessage">{formErrors.age}</span>
              )}
            </div>
            <div className="weight">
              <label htmlFor="bloodgroup">Blood Group</label>
              <input
                type="text"
                className={formErrors.bloodgroup.length > 0 ? "error" : null}
                placeholder="bloodgroup"
                name="bloodgroup"

                onChange={this.handleChange}
              />
              {formErrors.bloodgroup.length > 0 && (
                <span className="errorMessage">{formErrors.bloodgroup}</span>
              )}
            </div>
            <div className="days">
              <label htmlFor="gender">Gender</label>
              <input
                type="text"
                className={formErrors.gender.length < 1 ? "error" : null}
                placeholder="Days"
                name="gender"

                onChange={this.handleChange}
              />
              {formErrors.days.length > 0 && (
                <span className="errorMessage">{formErrors.days}</span>
              )}
            </div>
            <div className="city">
              <label htmlFor="city">City</label>
              <input
                className={formErrors.city.length > 0 ? "error" : null}
                placeholder="City"
                type="text"
                name="city"

                onChange={this.handleChange}
              />
              {formErrors.city.length > 0 && (
                <span className="errorMessage">{formErrors.city}</span>
              )}
            </div>
            <div className="email1">
              <label htmlFor="email1">Email Id</label>
              <input 
                className={formErrors.email.length >0 ?"error":null} 
                placeholder="Email" 
                type="email" 
                name="email" 
                onChange={this.handleChange}
              /> 
              {formErrors.email.length >0 && (
              <span className="errorMessage">{formErrors.email}</span>
              )}          
            </div>
            <div className="password1">
              <label htmlFor="password">Password</label>
                <input
                  className={formErrors.password.length >0 ?"error":null} 
                  placeholder="Password" 
                  type="password" 
                  name="password" 
                  noValidate
                  onChange={this.handleChange}
                />
                {formErrors.password.length >0 && (
                  <span className="errorMessage">{formErrors.password}</span>
                )}           
            </div>
            <div className="firstName">
              <label htmlFor="firstName">First Name</label>
              <input
                type="date"
               className={formErrors.name.length > 0 ? "error" : null}
                placeholder="First Name"
                name="firstName"
                
                onChange={this.handleChange}
              />
              {/* {formErrors.firstName.length > 0 && (
                <span className="errorMessage">{formErrors.firstName}</span>
              )} */}
            </div>


            <div className="createAccount">
              <button type="submit">Submit</button>
            </div>
          </form>
        </div>
      </div>
      </div>
	  
	  

    )


  }
}
export default SeekerRegistration;
