import React, { Component } from 'react';
import './Stylesheet.css';
import DonorService from '../services/DonorService';
import { withRouter } from 'react-router-dom';


const emailRegex = RegExp(
	/^[a-zA-Z0-9.!#$%&â€™*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/
);

const formValid = formErrors => {
	let valid = true;

	Object.values(formErrors).forEach(element => {
		element.length > 0 && (valid = false);
	});
	return valid;
};

class PatientRegister extends Component {


	constructor(props) {
		super(props)
		this.state = this.initialState;
		this.onChange = this.onChange.bind(this);
		this.inputRef = React.createRef();

	}

	initialState = {
		firstName: "",
		lastName: "",
		phoneNumber: "",
		age: "",
		bloodGroup: "",
		email: "",
		password: "",
		labConfirmedCovid: "",
		vaccineReceived: "",
		availability: "",
		followUp: "",
		//dateOfBirth: "",
		lastSymptomDate: "",
		testDate: "",
		pastDays: "",
		address: "",
		gender: "",
		registrationDate: "",
		formErrors: {
			firstName: "",
			lastName: "",
			phoneNumber: "",
			age: "",
			bloodGroup: "",
			email: "",
			password: "",
			labConfirmedCovid: "",
			vaccineReceived: "",
			availability: "",
			followUp: "",
			//dateOfBirth: "",
			lastSymptomDate: "",
			testDate: "",
			pastDays: "",
			address: "",
			gender: "",
			registrationDate: ""
		}


	}

	handleChangeForSelect = e =>{
		e.preventDefault();
		this.setState({ e });
	}

	handleChange = e => {
		e.preventDefault();
		const { name, value } = e.target;
		console.log('hello',name, value);
		let formErrors = this.state.formErrors;

		switch (name) {
			case 'firstName':
				formErrors.firstName = value.length < 3 ? "minimum 3 characters required" : "";
				break;
			case 'lastName':
				formErrors.lastName = value.length < 3 ? "minimum 3 characters required" : "";
				break;
			case 'phoneNumber':
				formErrors.phoneNumber = value.length < 10 ? "minimum 10 digits required" : "";
				break;
			case 'age':
				formErrors.age = value.length < 1 ? "minimum 2 digit required" : "";
				break;
			case 'bloodGroup':
				formErrors.bloodGroup = value.length < 1 ? "minimum 1 character required" : "";
				break;
			case 'pastDays':
				formErrors.days = value.length < 1 ? "minimum 1 digit required" : "";
				break;
			case 'address':
				formErrors.address = value.length < 2 ? "minimum 2 characters required" : "";
				break;
			case 'email':
				formErrors.email = emailRegex.test(value) ? "" : "Invalid Email";
				break;
			case 'password':
				formErrors.password = value.length < 6 ? "minimum 6 characters required" : "";
				break;

			// case 'labConfirmedCovid':
			// formErrors.labConfirmedCovid = value.length < 3 ? "minimum 3 characters required" : "";
			// break;   

			default:
				break;
		}

		this.setState({ formErrors, [name]: value }, () => console.log(this.state));
	};


	onChange = (e) => {
		this.setState({ [e.target.name]: e.target.selectedOption })
	}


	handleSubmit = submit => {
		submit.preventDefault();
		if (formValid(this.state.formErrors)) {
			// console.log(`
			// --SUBMITTING---
			// First Name: ${this.state.firstName}
			// Last Name: ${this.state.lastName}
			// ${JSON.stringify(this.state)}     
			// `);
			let info = {
				firstname: this.state.firstName,
				lastname: this.state.lastName,
				phoneNumber: this.state.phoneNumber,
				age: this.state.age,
				bloodGroup: this.bloodGroup.value,
				email: this.state.email,
				password: this.state.password,
				labConfirmedCovid: this.menu.value,
				vaccineReceived: this.vaccine.value,
				availability: this.availability.value,
				followUp: this.followUp.value,
				//dateOfBirth: this.state.//dateOfBirth,
				lastSymptomDate: this.state.lastSymptomDate,
				testDate: this.state.testDate,
				pastDays: this.pastDays.value,
				address: this.state.address,
				gender: this.gender.value,
				registrationDate: ""
			};
			console.log('harshal', info);
			// fetch('http://localhost:8080/api/patients/add',
			// {
			// method: 'POST',
			// headers: { 'Content-Type': 'application/json' },
			// body: JSON.stringify(info)
			// }).then(r => r.json()).then(res => {
			// alert("Thank you Registered Successfully ");
			// console.warn("Suceess!!");
			// })

			DonorService.createDonor(this.state).then(res => {
				this.props.history.push('/list');
				// console.log(res.data);
			});
		}
		else {
			console.error("Form Invalid");
		}
		
	};

	componentDidMount() {
		//this.inputRef.current.focus()
	}





	render() {
		const { formErrors } = this.state;
		return (
			<div>

				<div className="wrappers2">
					<div className="form-wrappers">
						<h2>Patient Registration</h2>
						<form onSubmit={this.handleSubmit} autoComplete="off">
							<div className="firstName">
								<label htmlFor="firstName">First Name</label>
								<input
									type="text"
									className={formErrors.firstName.length > 0 ? "error" : null}
									placeholder="First Name"
									name="firstName"
									//ref={this.inputRef}
									onChange={this.handleChange}
								/>
								{formErrors.firstName.length > 0 && (
									<span className="errorMessage">{formErrors.firstName}</span>
								)}
							</div>
							<div className="lastName">
								<label htmlFor="lastName">Last Name</label>
								<input
									type="text"
									className={formErrors.lastName.length > 0 ? "error" : null}
									placeholder="Last Name"
									name="lastName"

									onChange={this.handleChange}
								/>
								{formErrors.lastName.length > 0 && (
									<span className="errorMessage">{formErrors.lastName}</span>
								)}
							</div>
							<div className="email">
								<label htmlFor="email">Phone No:</label>
								<input
									className={formErrors.phoneNumber.length > 0 ? "error" : null}
									placeholder="Phone Number"
									type="tel"
									name="phoneNumber"
									onChange={this.handleChange}
								/>
								{formErrors.phoneNumber.length > 0 && (
									<span className="errorMessage">{formErrors.phoneNumber}</span>
								)}
							</div>
							<div className="age">
								<label htmlFor="age">Age</label>
								<input
									type="text"
									className={formErrors.age.length > 0 ? "error" : null}
									placeholder="Age"
									name="age"

									onChange={this.handleChange}
								/>
								{formErrors.firstName.length > 0 && (
									<span className="errorMessage">{formErrors.age}</span>
								)}
							</div>
							<div className="weight">
								<label htmlFor="bloodGroup">Blood Group</label>
								<select id="dropdown" ref={(input) => this.bloodGroup = input} onChange={this.handleChange.bind(this)} name='bloodGroup'>								<option value="A+">A+</option>
									<option value="" selected disabled>Select</option>
									<option value="O+">O+</option>
									<option value="O-">O-</option>
									<option value="A-">A-</option>
									<option value="B-">B-</option>
									<option value="B+">B+</option>
									<option value="AB+">AB+</option>
									<option value="AB-">AB-</option>
								</select>
								{formErrors.bloodGroup.length > 0 && (
									<span className="errorMessage">{formErrors.bloodGroup}</span>
								)}
							</div>
							{/* <div className="days">
								<label htmlFor="days">Recovery Days</label>
								<input
									type="text"
									className={formErrors.pastDays.length > 0 ? "error" : null}
									placeholder="Days"
									name="days"
									onChange={this.handleChange}
								/>
								{formErrors.pastDays.length > 0 && (
									<span className="errorMessage">{formErrors.pastDays}</span>
								)}
							</div> */}
							<div className="email1">
								<label htmlFor="email1">Email Id</label>
								<input
									className={formErrors.email.length > 0 ? "error" : null}
									placeholder="Email"
									type="email"
									name="email"
									onChange={this.handleChange}
								/>
								{formErrors.email.length > 0 && (
									<span className="errorMessage">{formErrors.email}</span>
								)}
								
							</div>
							<div className="password1">
								<label htmlFor="password">Password</label>
								<input
									className={formErrors.password.length > 0 ? "error" : null}
									placeholder="Password"
									type="password"
									name="password"
									noValidate
									onChange={this.handleChange}
								/>
								{formErrors.password.length > 0 && (
									<span className="errorMessage">{formErrors.password}</span>
								)}
							</div>


							<div className="labConfirmedCovid">
								<label htmlFor="labConfirmedCovid">Covid confirmed by authorized lab previously?</label>
								<select id="dropdown" ref={(input) => this.menu = input} name="labConfirmedCovid" onChange={this.handleChange.bind(this)}>
									<option value="" selected disabled>Select</option>
									<option value="1">YES</option>
									<option value="0">NO</option>
								</select>
								{formErrors.labConfirmedCovid.length > 0 && (
									<span className="errorMessage">{formErrors.labConfirmedCovid}</span>
								)}
							</div>

							<div className="vaccineReceived">
								<label htmlFor="vaccineReceived">Vaccine received?</label>
								<select id="dropdown" ref={(input) => this.vaccine = input} name="vaccineReceived" onChange={this.handleChange.bind(this)}>
									<option value="" selected disabled>Select</option>
									<option value="1">YES</option>
									<option value="0">NO</option>
								</select>
								{formErrors.vaccineReceived.length > 0 && (
									<span className="errorMessage">{formErrors.vaccineReceived}</span>
								)}
							</div>

							<div className="availability">
								<label htmlFor="availability">Are you available to donate right now?</label>
								<select id="dropdown" ref={(input) => this.availability = input} name="availability" onChange={this.handleChange.bind(this)}>
									<option value="" selected disabled>Select</option>
