import React, { Component } from 'react'
import DonorService from '../services/DonorService'

export default class ListDonorComponent extends Component {
	constructor(props) {
		super(props)
		this.state = {
			email:'',
			donor:[]
		}
		this.onChange = this.onChange.bind(this);
		this.searchByEmail = this.searchByEmail.bind(this);

	}

	onChange = (e) => {
		this.setState({ [e.target.name]: e.target.value })
	}

	// componentDidMount(){
	// 	DonorService.getDonors().then( (res) => {
	// 		this.setState({donor: res.data});
	// 	});
	// }

	searchByEmail(){
		// window.localStorage.setItem("emailId", this.email);
		// console.log(window.localStorage.getItem("emailId"));
		const email = this.state.email;
		DonorService.getDonor(email).then(res => {
			this.setState({donor: res.data});
			console.log(this.state.donor);
			window.localStorage.setItem("emailId", email);
			this.props.history.push('/list');
		})
	}

	render() {
		return (
			<div>
				{/* <h2 className="text-center">Donor List</h2> */}
				{/* <div className="row"> */}

					Email<input type="text" name="email" onChange= {this.onChange}></input>
					<button onClick={this.searchByEmail}>Search</button>
				</div>


			// </div>
		)
	}

}
