import React, { Component } from 'react';
import './Stylesheet.css';
import DonorService from '../services/DonorService';
import { withRouter } from 'react-router-dom';


const emailRegex = RegExp(
	/^[a-zA-Z0-9.!#$%&â€™*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/
);

const formValid = formErrors => {
	let valid = true;

	Object.values(formErrors).forEach(element => {
		element.length > 0 && (valid = false);
	});
	return valid;
};

class DonorRegister extends Component {


	constructor(props) {
		super(props)
		this.state = this.initialState;
		this.onChange = this.onChange.bind(this);
		this.inputRef = React.createRef();
		
	}

	initialState = {
		firstName: "",
		lastName: "",
		phoneNumber: "",
		//gender: "",
		email: "",
		password: "",
		stateName: "",
		age: "",
		bloodGroup: "",
		labConfirmedCovid: "",
		followUpNegReport: "",
		vaccineReceived: "",
		availability: "",
		//testDate: "",
		lastSymptomDate: "",
		past14Days: "",
		registrationDate: "",
		isChecked: true,
		formErrors: {
			firstName: "",
			lastName: "",
			phoneNumber: "",
			//gender: "",
			email: "",
			password: "",
			stateName: "",
			age: "",
			bloodGroup: "",
			labConfirmedCovid: "",
			followUpNegReport: "",
			vaccineReceived: "",
			availability: "",
			//testDate: "",
			lastSymptomDate: "",
			past14Days: "",
			registrationDate: ""
		}


	}

	toggleChange = () => {
		this.setState({
		  isChecked: !this.state.isChecked,
		});
	  }

	handleChangeForSelect = e =>{
		e.preventDefault();
		this.setState({ e });
	}

	handleChange = e => {
		e.preventDefault();
		const { name, value } = e.target;
		//console.log('hello',name, value);
		let formErrors = this.state.formErrors;

		switch (name) {
			case 'firstName':
				formErrors.firstName = value.length < 3 ? "Minimum 3 characters required" : "";
				break;
			case 'lastName':
				formErrors.lastName = value.length < 3 ? "Minimum 3 characters required" : "";
				break;
			case 'phoneNumber':
				formErrors.phoneNumber = value.length < 10 ? "Minimum 10 digits required" : "";
				break;
			// case 'gender':
			// 	formErrors.gender = value.length < 1 ? "Please choose an option" : "";
			// 	break;
			case 'age':
				formErrors.age = value.length < 2 ? "Minimum 2 digits required. Age should be between 18-60" : "";
				break;
			case 'bloodGroup':
				formErrors.bloodGroup = value === "select" ? "Please choose an option" : "";
				break;
			case 'stateName':
				formErrors.stateName = value === "select" ? "Please choose an option" : "";
				break;
			case 'email':
				formErrors.email = emailRegex.test(value) ? "" : "Invalid Email";
				break;
			case 'password':
				formErrors.password = value.length < 6 ? "Minimum 6 characters required" : "";
				break;
			case 'labConfirmedCovid':
				formErrors.labConfirmedCovid = value === "0" || value === "select" ? "You are not eligible to donate" : "";
				break;  
			case 'followUpNegReport':
				formErrors.followUpNegReport = value === "0" || value === "select"  ? "You are not eligible to donate" : "";
				break;
			case 'past14Days':
				formErrors.past14Days = value === "0" || value === "select" ? "You are not eligible to donate" : "";
				break;
			case 'availability':
				formErrors.availability = value === "select" ? "Please choose an option" : "";
				break;
			default:
				break;
		}


		this.setState({ formErrors, [name]: value }, () => console.log(this.state));
	};


	onChange = (e) => {
		this.setState({ [e.target.name]: e.target.selectedOption })
	}


	handleSubmit = submit => {
		submit.preventDefault();
		const { name, value } = submit.target;
		let formErrors = this.state.formErrors;
		switch (name) {
			case 'bloodGroup':
				formErrors.bloodGroup = value === "select" ? "Please choose an option" : "";
				break;
			case 'stateName':
				formErrors.stateName = value === "select" ? "Please choose an option" : "";
				break;
			case 'labConfirmedCovid':
				formErrors.labConfirmedCovid = value === "0" || value === "select" ? "You are not eligible to donate" : "";
				break;  
			case 'followUpNegReport':
				formErrors.followUpNegReport = value === "0" || value === "select"  ? "You are not eligible to donate" : "";
				break;
			case 'past14Days':
				formErrors.past14Days = value === "0" || value === "select" ? "You are not eligible to donate" : "";
				break;
			case 'availability':
				formErrors.availability = value === "select" ? "Please choose an option" : "";
				break;
			default:
				break;
		}
		if (formValid(this.state.formErrors)) {
			 
			let info = {
				firstname: this.state.firstName,
				lastname: this.state.lastName,
				phoneNumber: this.state.phoneNumber,
				//gender: this.gender.value,
				age: this.state.age,
				bloodGroup: this.bloodGroup.value,
				email: this.state.email,
				password: this.state.password,
				labConfirmedCovid: this.menu.value,
				followUpNegReport: this.followUpNegReport.value,
				vaccineReceived: this.vaccine.value,
				availability: this.availability.value,
				//testDate: this.state.testDate,
				lastSymptomDate: this.state.lastSymptomDate,
				past14Days: this.past14Days.value,
				stateName: this.stateName.value,
				registrationDate: ""
			};
			console.log('Donor-JSON', info);
			DonorService.createDonor(this.state).then(res => {
				if (res.data.statusCodeValue === 400){
					alert("user already exists")
				}
				else if(res.data.statusCodeValue === 202){
					alert("User created Successfuly")
					this.props.history.push('/registered_successfully');
				}
			});
		}
		else {
			alert("Please fill the form correctly!");
			console.error("Form Invalid");
		}
		
	};

	componentDidMount() {
		this.inputRef.current.focus()
	}


	render() {
		const { formErrors } = this.state;
		return (
			<div>

				<div className="wrappers2">
					<div className="form-wrappers">
						<h2>Donor Registration</h2>
						<form onSubmit={this.handleSubmit} autoComplete="off">
							<div className="name">
								<label htmlFor="firstName">First Name</label>
								<input
									type="text"
									className={formErrors.firstName.length > 0 ? "error" : null}
									placeholder="First Name"
									name="firstName"
									required
									ref={this.inputRef}
									onChange={this.handleChange}
								/>
								{formErrors.firstName.length > 0 && (
									<span className="errorMessage">{formErrors.firstName}</span>
								)}
							</div>
							<div className="name">
								<label htmlFor="lastName">Last Name</label>
								<input
									type="text"
									className={formErrors.lastName.length > 0 ? "error" : null}
									placeholder="Last Name"
									name="lastName"
									required
									onChange={this.handleChange}
								/>
								{formErrors.lastName.length > 0 && (
									<span className="errorMessage">{formErrors.lastName}</span>
								)}
							</div>
							{/* <div className="name">
								<label htmlFor="gender">Select your gender</label>
								<select defaultValue={'M'} required ref={(input) => this.gender = input} name="gender" onChange={this.handleChange.bind(this)}>
								<option value="null" disabled>Select</option>
									<option value="M" >MALE</option>
									<option value="F">FEMALE</option>
									<option value="O">OTHER</option>
								</select>
								{formErrors.gender.length > 0 && (
									<span className="errorMessage">{formErrors.gender}</span>
								)}
							</div> */}
							<div className="name">
								<label htmlFor="phoneNumber">Phone No</label>
								<input
									className={formErrors.phoneNumber.length > 0 ? "error" : null}
									placeholder="Phone Number"
									type="tel"
									name="phoneNumber"
									pattern="[0-9]{10}"
									required
									onChange={this.handleChange}
								/>
								{formErrors.phoneNumber.length > 0 && (
									<span className="errorMessage">{formErrors.phoneNumber}</span>
								)}
							</div>
							<div className="name">
								<label htmlFor="age">Your Age</label>
								<input
									type="number"
									min = "18"
									max = "60"
									size = "2"
									className={formErrors.age.length > 0 ? "error" : null}
									placeholder="Age"
									name="age"
									required
									onChange={this.handleChange}
								/>
								{formErrors.age.length > 2 && (
									<span className="errorMessage">{formErrors.age}</span>
								)}
							</div>
							<div className="name">
								<label htmlFor="email">Email Id</label>
								<input
									className={formErrors.email.length > 0 ? "error" : null}
									placeholder="Email"
									type="email"
									name="email"
									required
									onChange={this.handleChange}
								/>
								{formErrors.email.length > 0 && (
									<span className="errorMessage">{formErrors.email}</span>
								)}
							</div>
							<div className="name">
								<label htmlFor="password">Set a new Password</label>
								<input
									className={formErrors.password.length > 0 ? "error" : null}
									placeholder="Password"
									type="password"
									name="password"
									required
									noValidate
									onChange={this.handleChange}
								/>
								{formErrors.password.length > 0 && (
									<span className="errorMessage">{formErrors.password}</span>
								)}
							</div>
							<div className="name">
								<label htmlFor="stateName">Select a state from where you would like to donate</label>
								<select defaultValue={'select'} ref={(input) => this.stateName = input} required onChange={this.handleChange.bind(this)} name='stateName'>
									<option value="select" disabled>Select</option>
									<option value="Andhra Pradesh">Andhra Pradesh</option>
									<option value="Arunachal Pradesh">Arunachal Pradesh</option>
									<option value="Assam">Assam</option>
									<option value="Bihar">Bihar</option>
									<option value="Chhattisgarh">Chhattisgarh</option>
									<option value="Goa">Goa</option>
									<option value="Gujarat">Gujarat</option>
									<option value="Haryana">Haryana</option>
									<option value="Himachal Pradesh">Himachal Pradesh</option>
									<option value="Jammu and Kashmir">Jammu and Kashmir</option>
									<option value="Jharkhand">Jharkhand</option>
									<option value="Karnataka">Karnataka</option>
									<option value="Kerala">Kerala</option>
									<option value="Madhya Pradesh">Madhya Pradesh</option>
									<option value="Maharashtra">Maharashtra</option>
									<option value="Manipur">Manipur</option>
									<option value="Meghalaya">Meghalaya</option>
									<option value="Mizoram">Mizoram</option>
									<option value="Nagaland">Nagaland</option>
									<option value="Odisha">Odisha</option>
									<option value="Punjab">Punjab</option>
									<option value="Rajasthan">Rajasthan</option>
									<option value="Sikkim">Sikkim</option>
									<option value="Tamil Nadu">Tamil Nadu</option>
									<option value="Telangana">Telangana</option>
									<option value="Tripura">Tripura</option>
									<option value="Uttarakhand">Uttarakhand</option>
									<option value="Uttar Pradesh">Uttar Pradesh</option>
									<option value="West Bengal">West Bengal</option>
									<option value="Andaman and Nicobar Islands">Andaman and Nicobar Islands</option>
									<option value="Chandigarh">Chandigarh</option>
									<option value="Dadra and Nagar Haveli">Dadra and Nagar Haveli</option>
									<option value="Daman and Diu">Daman and Diu</option>
									<option value="Delhi">Delhi</option>
									<option value="Lakshadweep">Lakshadweep</option>
									<option value="Puducherry">Puducherry</option>	
								</select>
								{formErrors.stateName.length > 0 && (
									<span className="errorMessage">{formErrors.stateName}</span>
								)}
							</div>
							
							<div className="name">
								<label htmlFor="bloodGroup">Your Blood Group</label>
								<select defaultValue={'select'}required ref={(input) => this.bloodGroup = input} onChange={this.handleChange.bind(this)} name='bloodGroup'>
									<option value="select" disabled>Select</option>
									<option value="O+">O+</option>
									<option value="O-">O-</option>
									<option value="A-">A-</option>
									<option value="A+">A+</option>
									<option value="B-">B-</option>
									<option value="B+">B+</option>
									<option value="AB+">AB+</option>
									<option value="AB-">AB-</option>
								</select>
								{formErrors.bloodGroup.length > 0 && (
									<span className="errorMessage">{formErrors.bloodGroup}</span>
								)}
							</div>
							<div className="name">
								<label htmlFor="labConfirmedCovid">Covid confirmed by an authorized lab previously?</label>
								<select defaultValue={'select'} required ref={(input) => this.menu = input} name="labConfirmedCovid" onChange={this.handleChange.bind(this)}>
								<option value="select" disabled>Select</option>
									<option value="1" >YES</option>
									<option value="0">NO</option>
								</select>
								{formErrors.labConfirmedCovid.length > 0 && (
									<span className="errorMessage">{formErrors.labConfirmedCovid}</span>
								)}
							</div>
							<div className="name">
								<label htmlFor="followUpNegReport">Have you received your follow up negative report?</label>
								<select defaultValue={'select'} required ref={(input) => this.followUpNegReport = input} name="followUpNegReport" onChange={this.handleChange.bind(this)} required>
								<option value="select" disabled>Select</option>
									<option value="1">YES</option>
									<option value="0">NO</option>
								</select>
								{formErrors.followUpNegReport.length > 0 && (
									<span className="errorMessage">{formErrors.followUpNegReport}</span>
								)}
							</div>
							<div className="name">
								<label htmlFor="vaccineReceived">Are you vaccinated against Covid?</label>
								<select defaultValue={'select'} required ref={(input) => this.vaccine = input} name="vaccineReceived" onChange={this.handleChange.bind(this)}>
								<option value="select" disabled>Select</option>
									<option value="1">YES</option>
									<option value="0">NO</option>
								</select>
								{formErrors.vaccineReceived.length > 0 && (
									<span className="errorMessage">{formErrors.vaccineReceived}</span>
								)}
							</div>
							<div className="name">
								<label htmlFor="availability">Are you available to donate right now?</label>
								<select defaultValue={'select'} required ref={(input) => this.availability = input} name="availability" onChange={this.handleChange.bind(this)}>
									<option value="select" disabled>Select</option>
									<option value="1">YES</option>
									<option value="0">NO</option>
								</select>
								{formErrors.availability.length > 0 && (
									<span className="errorMessage">{formErrors.availability}</span>
								)}
							</div>
							{/* <div className="name">
								<label htmlFor="testDate">Last Covid test taken on</label>
								<input
									type="date"
									className={formErrors.testDate.length > 0 ? "error" : null}
									placeholder="Last Test Date"
									name="testDate"
									required
									onChange={this.handleChange}
								/>
								{formErrors.testDate.length > 0 && (
									<span className="errorMessage">{formErrors.testDate}</span>
								)}
							</div> */}
							<div className="name">
								<label htmlFor="lastSymptomDate">Last date of any symptom</label>
								<input
									type="date"
									className={formErrors.lastSymptomDate.length > 0 ? "error" : null}
									placeholder="Date of Birth"
									name="lastSymptomDate"
									required
									onChange={this.handleChange}
								/>
								{formErrors.lastSymptomDate.length > 0 && (
									<span className="errorMessage">{formErrors.lastSymptomDate}</span>
								)}
							</div>
							<div className="name">
								<label htmlFor="past14Days">Has is it been 14 days since you last observed any symptom?</label>
								<select defaultValue={'select'} required ref={(input) => this.past14Days = input} name="past14Days" onChange={this.handleChange.bind(this)}>
									<option value="select" disabled>Select</option>
									<option value="1">YES</option>
									<option value="0">NO</option>
								</select>
								{formErrors.past14Days.length > 0 && (
									<span className="errorMessage">{formErrors.past14Days}</span>
								)}
							</div>
							<div className="name">
								<label htmlFor="lastName">Last Name</label>
								<input
									type="checkbox"
									className={formErrors.lastName.length > 0 ? "error" : null}
									placeholder="Last Name"
									name="lastName"
									required
									onChange={this.handleChange}
								/>
								{formErrors.lastName.length > 0 && (
									<span className="errorMessage">{formErrors.lastName}</span>
								)}
							</div>
							<div>
							 defaultChecked={this.state.isChecked} onChange={this.toggleChange} 
							<input type="checkbox" >
							Hello	</input>
							</div>
							<div className="createAccount">
								<button type="submit">Submit</button>
							</div>
						</form>
					</div>
				</div>
			</div>
		)
	}
}
export default withRouter(DonorRegister);
