import React, { Component } from 'react'; 
import './Stylesheet.css';

const emailRegex = RegExp(
  /^[a-zA-Z0-9.!#$%&â€™*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/
);

const formValid = formErrors => {
  let valid = true;

  Object.values(formErrors).forEach(element => {
    element.length > 0 && (valid = false);
  });
  return valid;
};



class PatientRegister extends Component {


  constructor(props) {
    super(props)

    this.state = {
      firstName: "",
      lastName: "",
      phone: "",
      age: "",
      bloodgroup: "",
      days: "",
      city: "",
      email: "",
      password: "",
	  labConfirmedCovid: "",
	  VaccineReceived: "",
	  availability: "",
	  followUp: "",
	  dob: "",
	  symptonLastDate: "",
	  testDate: "",
	  pastDays: "",
	  address: "",
	  gender: "",
      formErrors: {
        firstName: "",
        lastName: "",
        phone: "",
        age: "",
        bloodgroup: "",
        days: "",
        city: "",
        email:"",
        password:"",
		labConfirmedCovid: "",
		VaccineReceived: "",
		availability: "",
		followUp: "",
		dob: "",
		symptonLastDate: "",
		testDate: "",
		pastDays: "",
		address: "",
		gender: ""
      }
    }
    //this.inputRef= React.createRef()

  }

  handleChange = e => {
    e.preventDefault();
    const { name, value } = e.target;
    let formErrors = this.state.formErrors;

    switch (name) {
      case 'firstName':
        formErrors.firstName = value.length < 3 ? "minimum 3 characters required" : "";
        break;
      case 'lastName':
        formErrors.lastName = value.length < 3 ? "minimum 3 characters required" : "";
        break;
      case 'phone':
        formErrors.phone = value.length < 10 ? "minimum 10 digits required" : "";
        break;
      case 'age':
        formErrors.age = value.length < 1 ? "minimum 2 digit required" : "";
        break;
      case 'bloodgroup':
        formErrors.bloodgroup = value.length < 1 ? "minimum 1 character required" : "";
        break;
      case 'days':
        formErrors.days = value.length < 1 ? "minimum 1 digit required" : "";
        break;
      case 'city':
        formErrors.city = value.length < 2 ? "minimum 2 characters required" : "";
        break;
      case 'email':
        formErrors.email = emailRegex.test(value) ? "" : "Invalid Email";
        break;
      case 'password':
        formErrors.password = value.length <6 ? "minimum 6 characters required" : "";
        break;
		
	  // case 'labConfirmedCovid':
        // formErrors.labConfirmedCovid = value.length < 3 ? "minimum 3 characters required" : "";
        // break;   
		
      default:
        break;
    }

    this.setState({ formErrors, [name]: value }, () => console.log(this.state));
  };

  handleSubmit = submit => {
     submit.preventDefault();
    if (formValid(this.state.formErrors)) {
      // console.log(`
          // --SUBMITTING---
          // First Name: ${this.state.firstName}
          // Last Name: ${this.state.lastName}
          // ${JSON.stringify(this.state)}     
      // `);
      let info = {
        firstname: this.state.firstName,
        lastname: this.state.lastName,
        phone: this.state.phone,
        age: this.state.age,
        bloodgroup: this.bloodgroup.value,
        days: this.state.days,
        city: this.state.city,
        email: this.state.email,
        password: this.state.password,
		labConfirmedCovid: this.menu.value,
		VaccineReceived: this.vaccine.value,
		availability: this.availability.value,
		followUp: this.followUp.value,
		dob: this.state.dob,
		symptonLastDate: this.state.symptonLastDate,
		testDate: this.state.testDate,
		pastDays: this.pastDays.value,
		address: this.state.address,
		gender: this.gender.value
      };
	  console.log('harshal',info);
      // fetch('http://localhost:8080/api/patients/add',
        // {
          // method: 'POST',
          // headers: { 'Content-Type': 'application/json' },
          // body: JSON.stringify(info)
        // }).then(r => r.json()).then(res => {
            // alert("Thank you Registered Successfully ");
            // console.warn("Suceess!!");
        // })

    }
    else {
      console.error("Form Invalid");
    }
  };

  componentDidMount(){
    //this.inputRef.current.focus()
  }


  render() {
    const { formErrors } = this.state;
    return (
      <div>
	
      <div className="wrappers2">
        <div className="form-wrappers">
          <h2>Patient Registration</h2>
          <form onSubmit={this.handleSubmit} autoComplete="off">
            <div className="firstName">
              <label htmlFor="firstName">First Name</label>
              <input
                type="text"
                className={formErrors.firstName.length > 0 ? "error" : null}
                placeholder="First Name"
                name="firstName"
               // ref={this.inputRef}
                onChange={this.handleChange}
              />
              {formErrors.firstName.length > 0 && (
                <span className="errorMessage">{formErrors.firstName}</span>
              )}
            </div>
            <div className="lastName">
              <label htmlFor="lastName">Last Name</label>
              <input
                type="text"
                className={formErrors.lastName.length > 0 ? "error" : null}
                placeholder="Last Name"
                name="lastName"

                onChange={this.handleChange}
              />
              {formErrors.lastName.length > 0 && (
                <span className="errorMessage">{formErrors.lastName}</span>
              )}
            </div>
            <div className="email">
              <label htmlFor="email">Phone No:</label>
              <input
                className={formErrors.phone.length > 0 ? "error" : null}
                placeholder="phone"
                type="text"
                name="phone"
                onChange={this.handleChange}
              />
              {formErrors.phone.length > 0 && (
                <span className="errorMessage">{formErrors.phone}</span>
              )}
            </div>
            <div className="age">
              <label htmlFor="age">Age</label>
              <input
                type="text"
                className={formErrors.age.length > 0 ? "error" : null}
                placeholder="Age"
                name="age"

                onChange={this.handleChange}
              />
              {formErrors.firstName.length > 0 && (
                <span className="errorMessage">{formErrors.age}</span>
              )}
            </div>
            <div className="weight">
              <label htmlFor="bloodgroup">Blood Group</label>
              <select id = "dropdown" ref = {(input)=> this.bloodgroup = input}>
				<option value="A+">A+</option>
				<option value="O+">O+</option>
				<option value="B+">B+</option>
				<option value="AB+">AB+</option>
				<option value="A-">A-</option>
				<option value="O-">O-</option>
				<option value="B-">B-</option>
				<option value="AB-">AB-</option>
			  </select>
              {formErrors.bloodgroup.length > 0 && (
                <span className="errorMessage">{formErrors.bloodgroup}</span>
              )}
            </div>
            <div className="days">
              <label htmlFor="days">Recovery Days</label>
              <input
                type="text"
                className={formErrors.days.length > 0 ? "error" : null}
                placeholder="Days"
                name="days"

                onChange={this.handleChange}
              />
              {formErrors.days.length > 0 && (
                <span className="errorMessage">{formErrors.days}</span>
              )}
            </div>
            <div className="city">
              <label htmlFor="city">City</label>
              <input
                className={formErrors.city.length > 0 ? "error" : null}
                placeholder="City"
                type="text"
                name="city"

                onChange={this.handleChange}
              />
              {formErrors.city.length > 0 && (
                <span className="errorMessage">{formErrors.city}</span>
              )}
            </div>
            <div className="email1">
              <label htmlFor="email1">Email Id</label>
              <input 
                className={formErrors.email.length >0 ?"error":null} 
                placeholder="Email" 
                type="email" 
                name="email" 
                onChange={this.handleChange}
              /> 
              {formErrors.email.length >0 && (
              <span className="errorMessage">{formErrors.email}</span>
              )}          
            </div>
            <div className="password1">
              <label htmlFor="password">Password</label>
                <input
                  className={formErrors.password.length >0 ?"error":null} 
                  placeholder="Password" 
                  type="password" 
                  name="password" 
                  noValidate
                  onChange={this.handleChange}
                />
                {formErrors.password.length >0 && (
                  <span className="errorMessage">{formErrors.password}</span>
                )}           
            </div>
			
			
		   <div className="labConfirmedCovid">
              <label htmlFor="labConfirmedCovid">labConfirmedCovid</label>
              <select id = "dropdown" ref = {(input)=> this.menu = input}>
				<option value="1">YES</option>
				<option value="0">NO</option>
			  </select>
              {formErrors.labConfirmedCovid.length > 0 && (
                <span className="errorMessage">{formErrors.labConfirmedCovid}</span>
              )}
            </div>
			
			<div className="VaccineReceived">
              <label htmlFor="VaccineReceived">VaccineReceived</label>
              <select id = "dropdown" ref = {(input)=> this.vaccine = input}>
				<option value="1">YES</option>
				<option value="0">NO</option>
			  </select>
              {formErrors.VaccineReceived.length > 0 && (
                <span className="errorMessage">{formErrors.VaccineReceived}</span>
              )}
            </div>
			
			
			
			
			<div className="availability">
              <label htmlFor="availability">Availability</label>
              <select id = "dropdown" ref = {(input)=> this.availability = input}>
				<option value="1">YES</option>
				<option value="0">NO</option>
			  </select>
              {formErrors.availability.length > 0 && (
                <span className="errorMessage">{formErrors.availability}</span>
              )}
            </div>
            <div className="followUp">
              <label htmlFor="followUp">followUp</label>
			  <select id = "dropdown" ref = {(input)=> this.followUp = input}>
				<option value="1">YES</option>
				<option value="0">NO</option>
			  </select>
              {formErrors.followUp.length > 0 && (
                <span className="errorMessage">{formErrors.followUp}</span>
              )}
			</div>
			  
			
			 <div className="dob">
              <label htmlFor="dob">Date of birth</label>
              <input
                type="date"
                className={formErrors.dob.length > 0 ? "error" : null}
                placeholder="Date of Birth"
                name="dob"
                //ref={this.inputRef}
                onChange={this.handleChange}
              />
              {formErrors.dob.length > 0 && (
                <span className="errorMessage">{formErrors.dob}</span>
              )}
            </div>
			 <div className="symptonLastDate">
              <label htmlFor="symptonLastDate">Sympton Last Date</label>
              <input
                type="date"
                className={formErrors.symptonLastDate.length > 0 ? "error" : null}
                placeholder="Date of Birth"
                name="symptonLastDate"
                //ref={this.inputRef}
                onChange={this.handleChange}
              />
              {formErrors.symptonLastDate.length > 0 && (
                <span className="errorMessage">{formErrors.symptonLastDate}</span>
              )}
            </div>
			
			<div className="pastDays">
              <label htmlFor="pastDays">pastDays</label>
              <select id = "dropdown" ref = {(input)=> this.pastDays = input}>
				<option value="1">YES</option>
				<option value="0">NO</option>
			  </select>
              {formErrors.pastDays.length > 0 && (
                <span className="errorMessage">{formErrors.pastDays}</span>
              )}
            </div>
			
			<div className="testDate">
              <label htmlFor="testDate">testDate</label>
              <input
                type="date"
                className={formErrors.testDate.length > 0 ? "error" : null}
                placeholder="testDate"
                name="testDate"
               // ref={this.inputRef}
                onChange={this.handleChange}
              />
              {formErrors.testDate.length > 0 && (
                <span className="errorMessage">{formErrors.testDate}</span>
              )}
            </div>
			
			<div className="address">
              <label htmlFor="address">address</label>
              <input
                type="text"
                className={formErrors.address.length > 0 ? "error" : null}
                placeholder="address"
                name="address"
               // ref={this.inputRef}
                onChange={this.handleChange}
              />
              {formErrors.address.length > 0 && (
                <span className="errorMessage">{formErrors.address}</span>
              )}
            </div>
			
			<div className="gender">
              <label htmlFor="gender">Gender</label>
              <select id = "dropdown" ref = {(input)=> this.gender = input}>
				<option value="Male">Male</option>
				<option value="Female">Female</option>
				<option value="NotSpecified">Prefer not to say</option>
			  </select>
              {formErrors.gender.length > 0 && (
                <span className="errorMessage">{formErrors.gender}</span>
              )}
            </div>
			
            <div className="createAccount">
              <button type="submit">Submit</button>
            </div>
          </form>
        </div>
      </div>
      </div>
	  
	  

    )


  }
}
export default PatientRegister;
